mapper.select:
SELECT * FROM CUSTOMER_SERVICE WHERE ADMIN_USER_IDX IS NOT NULL
AND CUSTOMER_SERVICE_TYPE = #{type}
ORDER BY CUSTOMER_SERVICE_REGDATE DESC OFFSET #{start} ROWS FETCH NEXT #{size} ROWS ONLY

mapper.insert:
INSERT INTO CUSTOMER_SERVICE 
	( 
	  CUSTOMER_SERVICE_IDX, CUSTOMER_SERVICE_TITLE 
  ,CUSTOMER_SERVICE_TYPE, CUSTOMER_SERVICE_REGDATE 
  ,CUSTOMER_SERVICE_CONTENT 
  ,ADMIN_USER_IDX
 ) 
VALUES 
	( 
	 (SELECT NVL(MAX(customer_service_idx),0)+1 FROM CUSTOMER_SERVICE ) 
	 ,#{customer_service_title} 
  ,#{customer_service_type}
  , SYSDATE
  ,#{customer_service_content}
  ,1
 )

mapper.update:
UPDATE CUSTOMER_SERVICE
SET CUSTOMER_SERVICE_TITLE = #{customer_service_title},
    CUSTOMER_SERVICE_CONTENT = #{customer_service_content},
    CUSTOMER_SERVICE_TYPE = #{customer_service_type}
WHERE CUSTOMER_SERVICE_IDX = #{customer_service_idx}

mapper.select:
SELECT * FROM CUSTOMER_SERVICE WHERE USER_IDX IS NOT NULL
AND CUSTOMER_SERVICE_TYPE = #{type}
ORDER BY CASE WHEN LENGTH(CUSTOMER_SERVICE_CONTENT) = 0 THEN 1 WHEN CUSTOMER_SERVICE_CONTENT IS NULL THEN 1 ELSE 2 END ASC, CUSTOMER_SERVICE_REGDATE DESC OFFSET #{start} ROWS FETCH NEXT #{size} ROWS ONLY

mapper.update:
UPDATE CUSTOMER_SERVICE
SET CUSTOMER_SERVICE_TITLE = #{customer_service_title},
    CUSTOMER_SERVICE_CONTENT = #{customer_service_content},
    CUSTOMER_SERVICE_TYPE = #{customer_service_type}
WHERE CUSTOMER_SERVICE_IDX = #{customer_service_idx}

mapper.select:
SELECT * FROM NOTICE
WHERE NOTICE_TYPE = #{type}
ORDER BY NOTICE_IDX DESC OFFSET #{start} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT COUNT(*)
FROM NOTICE

    WHERE NOTICE_TYPE = #{type}

mapper.update:
UPDATE NOTICE
SET NOTICE_TITLE = #{notice_title},
    NOTICE_CONTENT = #{notice_content},
    NOTICE_TYPE = #{notice_type}
WHERE NOTICE_IDX = #{notice_idx}

mapper.insert:
INSERT INTO NOTICE 
	( 
	  NOTICE_IDX, NOTICE_TITLE 
  ,NOTICE_TYPE, NOTICE_REGDATE 
  ,NOTICE_CONTENT 
 ) 
VALUES 
	( 
	 (SELECT NVL(MAX(notice_idx),0)+1 FROM NOTICE ) 
	 ,#{notice_title} 
  ,#{notice_type}
  , SYSDATE
  ,#{notice_content}
 )
