mapper.select:
SELECT P.PRODUCT_IDX, P.PRODUCT_NAME, TO_CHAR(P.PRODUCT_DESCRIPTION) PRODUCT_DESCRIPTION , P.PRICE, P.CATEGORY_IDX, P.CREATED_AT, P.UPDATED_AT, PI.PRODUCT_IMAGE_IDX, PI.PRODUCT_IMAGE_NAME, PI.PRODUCT_IMAGE_EXT, PI.PRODUCT_SFILE_NAME FROM PRODUCT P JOIN PRODUCT_IMAGE PI ON P.PRODUCT_IDX = PI.PRODUCT_IDX LEFT JOIN PRODUCT_ATTRIBUTE PA ON P.PRODUCT_IDX = PA.PRODUCT_IDX WHERE P.CATEGORY_IDX = #{category}
AND PA.CATEGORY_ATTRIBUTE_IDX IN
#{ categoryAttributeIdxs }
AND PA.CATEGORY_ATTRIBUTE_VALUE_IDX IN
#{ categoryAttributeValueIdxs }
GROUP BY P.PRODUCT_IDX, P.PRODUCT_NAME, TO_CHAR(P.PRODUCT_DESCRIPTION) , P.PRICE, P.CATEGORY_IDX, P.CREATED_AT, P.UPDATED_AT, PI.PRODUCT_IMAGE_IDX, PI.PRODUCT_IMAGE_NAME, PI.PRODUCT_IMAGE_EXT, PI.PRODUCT_SFILE_NAME ORDER BY P.PRODUCT_IDX ASC OFFSET #{ startRow } ROWS FETCH NEXT #{ endRow } ROWS ONLY

SELECT COUNT(DISTINCT P.PRODUCT_IDX) FROM PRODUCT P JOIN PRODUCT_IMAGE PI ON P.PRODUCT_IDX = PI.PRODUCT_IDX LEFT JOIN PRODUCT_ATTRIBUTE PA ON P.PRODUCT_IDX = PA.PRODUCT_IDX WHERE P.CATEGORY_IDX = #{category}
AND PA.CATEGORY_ATTRIBUTE_IDX IN
#{ categoryAttributeIdxs }
AND PA.CATEGORY_ATTRIBUTE_VALUE_IDX IN
#{ categoryAttributeValueIdxs }

mapper.insert:
INSERT INTO PRODUCT_ATTRIBUTE (
    PRODUCT_IDX,
    CATEGORY_ATTRIBUTE_IDX,
    CATEGORY_ATTRIBUTE_VALUE_IDX
) VALUES (
    #{PRODUCT_IDX},
    #{CATEGORY_ATTRIBUTE_IDX} ,
    #{CATEGORY_ATTRIBUTE_VALUE_IDX}

)
